import React from 'react';
import '../ClientFeedBack/ClientFeedBack.css';

const ClientFeedBack = ({ client }) => {
    return (
        <section className="text-center">
            <h2 className="font-weight">Clients FeedBack</h2>
            <div className="col-md-3 customer">
                <div class="card text-white bg-light" style={{ maxWidth: '22rem' }}>
                    <div class="title d-flex align-items-center p-3 text-dark">
                        <img src={client.clientImg} alt="customer" style={{ width: '80px' }} />
                        <h5 className="card-title p-3">{client.clientName}
                            <p><small className="font-weight-bold">{client.clientPosition}</small></p></h5>
                    </div>
                    <div class="card-body text-dark mb-2">
                        <p class="card-text">{client.description}</p>
                    </div>
                </div>
            </div>

        </section>

    );
};

export default ClientFeedBack;
