import React from 'react';
import '../ClientFeedBack/ClientFeedBack.css';

const ClientFeedBack = ({ client }) => {
    return (  
            <div className="col-md-3">
                <div class="card bg-light customer p-2" style={{ maxWidth: '22rem' }}>
                    <div class="title d-flex align-items-center text-dark ml-3">
                        <img src={client.clientImg} alt="customer" style={{ width: '60px' }} />
                        <h6 className="card-title ml-3 mt-3">{client.clientName}
                            <p><small className="font-weight-bold">{client.clientPosition}</small></p></h6>
                    </div>
                    <div class="mt-3 text-dark">
                        <p class="card-text">{client.description}</p>
                    </div>
                </div>
            </div>

        

    );
};

export default ClientFeedBack;
