import React from 'react';
import Sidebar from '../Sidebar/Sidebar';

const Order = () => {
    const handleBlur = () => {
        console.log('input')
    }

    const handleSubmit = () => {
        console.log('submitted')
    }
    return (
        <section className="container-fluid row " >
            <div className="col-md-5">
            <Sidebar></Sidebar>
            </div>
            
            <div className="col-md-7 p-4 pr-5" style={{ position: "absolute", right: 0, backgroundColor: "#F4FDFB" }}>
                <h5 className="text-brand">Add A Doctor</h5>
                <form onSubmit={handleSubmit}>
                    <div class="form-group">
                        <label for="email">Email address</label>
                        <input onBlur={handleBlur} type="email" class="form-control" placeholder="Enter email" name="email" />
                    </div>
                    <div class="form-group">
                        <label for="name">Name</label>
                        <input onBlur={handleBlur} type="text" class="form-control" placeholder="Enter password" name="name" />
                    </div>
                    <div class="form-group">
                        <label for="image">Upload Image</label>
                        <input  type="file" class="form-control" placeholder="picture" />
                    </div>
                    <button type="submit" class="btn btn-info">Submit</button>
                </form>
            </div>
        </section>
    );
};

export default Order;
